#!/bin/bash\n\necho \"🚀 Setting up DealLens AI M&A Screener...\"\n\n# Copy environment file\necho \"📝 Setting up environment variables...\"\ncp .env.example .env\necho \"✅ Environment file created. Please edit .env with your configuration.\"\n\n# Install root dependencies\necho \"📦 Installing root dependencies...\"\nnpm install\n\n# Install web app dependencies\necho \"📦 Installing web app dependencies...\"\ncd apps/web && npm install && cd ../..\n\n# Set up database package\necho \"📦 Installing database package dependencies...\"\ncd packages/db && npm install && cd ../..\n\n# Start infrastructure services\necho \"🐳 Starting infrastructure services (PostgreSQL, Redis)...\"\ndocker-compose up -d postgres redis\n\n# Wait for postgres to be ready\necho \"⏳ Waiting for PostgreSQL to be ready...\"\nsleep 10\n\n# Generate Prisma client\necho \"🔧 Generating Prisma client...\"\nnpm run db:generate\n\n# Push database schema\necho \"🗄️ Setting up database schema...\"\nnpm run db:push\n\n# Seed database (optional)\necho \"🌱 Would you like to seed the database with sample data? (y/n)\"\nread -r seed_choice\nif [ \"$seed_choice\" = \"y\" ] || [ \"$seed_choice\" = \"Y\" ]; then\n    npm run db:seed\nfi\n\necho \"✨ Setup complete!\"\necho \"\"\necho \"🎯 Quick Start Commands:\"\necho \"  npm run dev          - Start all development servers\"\necho \"  npm run docker:up    - Start all services in Docker\"\necho \"  npm run docker:logs  - View Docker logs\"\necho \"\"\necho \"🌐 Services will be available at:\"\necho \"  Frontend (Next.js):  http://localhost:3000\"\necho \"  API (FastAPI):       http://localhost:8000\"\necho \"  API Docs:            http://localhost:8000/docs\"\necho \"\"\necho \"📖 Check README.md for detailed documentation.\"\necho \"🎉 Happy coding!\""
